import React from "react";

// reactstrap components
import {
  Badge,
  Card,
  CardHeader,
  CardFooter,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Media,
  Pagination,
  PaginationItem,
  PaginationLink,
  Progress,
  Table,
  Container,
  Row,
  UncontrolledTooltip, Button
} from "reactstrap";
// core components
import Header from "components/Headers/Header.jsx";
import axios from "axios";
import App from "../../layouts/Pagination.jsx"

class Tables extends React.Component {
  constructor(props) {
    super(props);
    this.state ={
      isLoading: true,
      matchIdList:'',
      matchResult:[],
      activePage:1
    };
  }

  componentDidMount= async () => {
      await this._getMatchIdList();
      await this._getMatchIdDetail();
  };

  _getMatchIdList = () => {
      const search = this.props.location.search;
      const params = new URLSearchParams(search);
      const accessId = params.get("accessId");
      const matchtype = 50;                              //50: 공식 경기, 52: 감독 모드
      const offset = 0;                                  // 리스트에서 가져올 시작 위치
      const limit = 100;                                 // 리스트에서 가져올 갯수

      let getMatchIdList = 'https://api.nexon.co.kr/fifaonline4/v1.0/users/' + accessId + '/matches?matchtype=' + matchtype + '&offset=' + offset +'&limit=' + limit;
      try{
          return axios.get(getMatchIdList, {
              // 헤더 값 : 권한 시리얼 정보
              headers : { Authorization : 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhY2NvdW50X2lkIjoiMTIyNDc2MTUyOSIsImF1dGhfaWQiOiIyIiwidG9rZW5fdHlwZSI6IkFjY2Vzc1Rva2VuIiwic2VydmljZV9pZCI6IjQzMDAxMTQ4MSIsIlgtQXBwLVJhdGUtTGltaXQiOiIyMDAwMDoxMCIsIm5iZiI6MTU3NzAwODc3MywiZXhwIjoxNjQwMDgwNzczLCJpYXQiOjE1NzcwMDg3NzN9.Pv1OIow11dye_uv69wnVleR93fa4fDrmup1oTXVuUuo'}
          }).then(response =>
            this.setState({
              matchIdList: response.data
            })
          )
      } catch (error) {
          console.error(error);
      }
  };

  _getMatchIdDetail = () => {
    let MatchIdList = this.state.matchIdList;
    let matchResultArray = [];

    for (let i = 0; i < MatchIdList.length; i++) {
      if(MatchIdList.length === 0){
        return;
      }else{
        let getMatchIdDetail = 'https://api.nexon.co.kr/fifaonline4/v1.0/matches/' + MatchIdList[i];
        try {
          axios.get(getMatchIdDetail, {
            // 헤더 값 : 권한 시리얼 정보
            headers: {Authorization: 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJhY2NvdW50X2lkIjoiMTIyNDc2MTUyOSIsImF1dGhfaWQiOiIyIiwidG9rZW5fdHlwZSI6IkFjY2Vzc1Rva2VuIiwic2VydmljZV9pZCI6IjQzMDAxMTQ4MSIsIlgtQXBwLVJhdGUtTGltaXQiOiIyMDAwMDoxMCIsIm5iZiI6MTU3NzAwODc3MywiZXhwIjoxNjQwMDgwNzczLCJpYXQiOjE1NzcwMDg3NzN9.Pv1OIow11dye_uv69wnVleR93fa4fDrmup1oTXVuUuo'}
          }).then(response => {
            matchResultArray.push(response.data);
              this.setState({
                matchResult: matchResultArray,
                isLoading: false
              })
          })
        } catch (error) {
          console.error(error);
        }
      }
    }
  };

  handleButton = (matchResult) => {
    //console.log("handleButton-->", matchResult);

    if(matchResult === "승"){
      return <Button
          color="success"
          size="sm"
      >
      </Button>
    }else if(matchResult === "패"){
      return <Button
          color="warning"
          size="sm"
      >
      </Button>
    }else if(matchResult === "무"){
        return <Button
            color="secondary"
            size="sm"
        >
        </Button>
    }
  };

  handlePageChange = (pageNumber) => {
      console.log(`active page is ${pageNumber}`);
      this.setState(({activePage: pageNumber}));
  };

  render() {
    const { matchIdList, matchResult, isLoading, activePage} = this.state;
    if(!isLoading){
      console.log("matchList-->", matchResult[0].matchInfo[0].nickname, matchResult[0].matchInfo[1].nickname, isLoading);
    }

    return (
      <>
        <Header />
        {/* Page content */}
        <Container className="mt--7" fluid>
          {/* Table */}
          <Row>
            <div className="col">
              <Card className="shadow">
                <CardHeader className="border-0">
                  <h3 className="mb-0">경기 매치 리스트</h3>
                </CardHeader>
                <Table className="align-items-center table-flush" responsive>
                  <thead className="thead-light">
                    <tr>
                      <th scope="col">매치 결과</th>
                      <th scope="col">매치 날짜</th>
                      <th scope="col">Status</th>
                      <th scope="col">경기 결과</th>
                    </tr>
                  </thead>
                  {isLoading ? (
                      <div className="loader">
                        <span className="loader__text">Loading...</span>
                      </div>
                        ) : (
                  <tbody>
                  {matchResult.map((index, matchId) => (
                      <tr>
                        <th scope="row">
                          {index.matchInfo[0].nickname} vs {index.matchInfo.nickname}
                        </th>
                        <th>
                            {index.matchDate}
                        </th>
                        <th scope="row">
                          {index.matchInfo[0].accessId}
                        </th>
                        <th>
                          <Button color="secondary" onClick={this.handleButton(index.matchInfo[0].matchDetail.matchResult)}>
                            {index.matchInfo[0].matchDetail.matchResult}
                          </Button>
                        </th>
                      </tr>
                  ))}
                  </tbody>
                  )}
                </Table>
                <CardFooter className="py-4">
                  <nav aria-label="...">
                    <Pagination
                        activePage={activePage}
                      className="pagination justify-content-end mb-0"
                      listClassName="justify-content-end mb-0"
                    >
                      <PaginationItem className="disabled">
                        <PaginationLink
                          href="#pablo"
                          onClick={e => e.preventDefault()}
                          tabIndex="-1"
                        >
                          <i className="fas fa-angle-left" />
                          <span className="sr-only">Previous</span>
                        </PaginationLink>
                      </PaginationItem>
                      <PaginationItem className="active">
                        <PaginationLink
                          href="#pablo"
                          onClick={this.handlePageChange.bind(this)}
                        >
                          1
                        </PaginationLink>
                      </PaginationItem>
                      <PaginationItem>
                        <PaginationLink
                          href="#pablo"
                          onClick={this.handlePageChange.bind(this)}
                        >
                          2 <span className="sr-only">(current)</span>
                        </PaginationLink>
                      </PaginationItem>
                      <PaginationItem>
                        <PaginationLink
                          href="#pablo"
                          onClick={e => e.preventDefault()}
                        >
                          3
                        </PaginationLink>
                      </PaginationItem>
                      <PaginationItem>
                        <PaginationLink
                          href="#pablo"
                          onClick={e => e.preventDefault()}
                        >
                          <i className="fas fa-angle-right" />
                          <span className="sr-only">Next</span>
                        </PaginationLink>
                      </PaginationItem>
                    </Pagination>
                  </nav>
                </CardFooter>
              </Card>
            </div>
          </Row>
        </Container>
      </>
    );
  }
}

export default Tables;
