import React from "react";
import axios from "axios";

class SeasonList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            seasonResult: []
        }
    }

    componentDidMount = async () => {
        await this.getSeasonIdList();
    }

    getSeasonIdList = () => {
        const proxyurl = "https://cors-anywhere.herokuapp.com/";
        const getSeasonIdDetail = 'https://static.api.nexon.co.kr/fifaonline4/latest/seasonid.json';

        axios.get(proxyurl + getSeasonIdDetail).then(response => {
            let data = response.data;
            console.log("data:" , data[0].seasonId, data.length);

            let seasonIdList = [];
            let seasonImgList = [];
            let classNameList = [];

            for(let i = 0; i < data.length; i++){
                seasonIdList.push(data[i].seasonId);
                seasonImgList.push(data[i].seasonImg);
                classNameList.push(data[i].className);
            }

            console.log("data[i].seasonId:", seasonIdList);

            this.setState({
                seasonId: seasonIdList,
                seasonImg: seasonImgList,
                className: classNameList
            });

            console.log("this.state:", this.state.seasonId);
        })
    }

    render() {
        return (
            <div className="seasonList">
                {this.state.seasonResult.map((season, index)  => {
                    return (
                        <div className="name">
                            {this.state.seasonId[index]} - {this.state.seasonImg[index]} -
                            {this.state.className[index]} - <img src= {this.state.seasonImg[index]}/>
                        </div>
                    )})}
            </div>
        );
    }
}

export default SeasonList;
